describe PlanetsController do

  # This should return the minimal set of attributes required to create a valid
  # Planet. As you add validations to Planet, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all planets as @planets" do
      planet = Planet.create! valid_attributes
      get :index
      assigns(:planets).should eq([planet])
    end
  end

  describe "GET show" do
    it "assigns the requested planet as @planet" do
      planet = Planet.create! valid_attributes
      get :show, :id => planet.id.to_s
      assigns(:planet).should eq(planet)
    end
  end

  describe "GET new" do
    it "assigns a new planet as @planet" do
      get :new
      assigns(:planet).should be_a_new(Planet)
    end
  end

  describe "GET edit" do
    it "assigns the requested planet as @planet" do
      planet = Planet.create! valid_attributes
      get :edit, :id => planet.id.to_s
      assigns(:planet).should eq(planet)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Planet" do
        expect {
          post :create, :planet => valid_attributes
        }.to change(Planet, :count).by(1)
      end

      it "assigns a newly created planet as @planet" do
        post :create, :planet => valid_attributes
        assigns(:planet).should be_a(Planet)
