        assigns(:planet).should be_a_new(Planet)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Planet.any_instance.stub(:save).and_return(false)
        post :create, :planet => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested planet" do
        planet = Planet.create! valid_attributes
        # Assuming there are no other planets in the database, this
        # specifies that the Planet created on the previous line
        # receives the :update_attributes message with whatever params are
